mapper.select:
SELECT 

FROM c_ins_business_zdgcxm a

WHERE a.id = #{id}

SELECT
FROM c_ins_business_zdgcxm a,c_ins_business_info info WHERE a.cbsnum = info.cbsnum and (a.status='1' or a.status='2') and (case when a.status='1' then a.CSUSERID when a.status='2' then a.FSUSERID end)=#{currentuserid}
and info.projectname like '%'||#{projectname}||'%' CONCAT('%', #{projectname}, '%')
and a.zdxmlx=#{zdxmlx}
ORDER BY ${page.orderBy}

SELECT 

FROM c_ins_business_zdgcxm a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE c_ins_business_zdgcxm SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
